import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { lastValueFrom } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthServiceService {
  token: string = "";
  message: string = "";
  constructor(private http: HttpClient) { }

  async getToken(): Promise<string> {
    const promise = new Promise<string>((resolve, reject) => {
      var params = new HttpParams().set("username", "admin").set("password", "password");
      this.http.get("https://localhost:7054/token", { params }).subscribe({
        next: (res: any) => {
          resolve(res.token);
        },
        error: (err:any) => {
          reject(err);
        }
      })
    });
    return promise;
  }

  async getAdminOnly() {
    try {
      var messageObj = await lastValueFrom(this.http.get("https://localhost:7054/MyAPI/admin-only"));
      console.log(messageObj);
    }
    catch (error) {
      console.error(error);
    }
  }

  async getAdminOrManager() {
    try {
      this.message = await lastValueFrom(this.http.get("https://localhost:7054/MyAPI/manager-or-admin")) as string;
    }
    catch (error) {
      console.error(error);
    }
  }

  async getAuthenticatedUser() {
    try {
      this.message = await lastValueFrom(this.http.get("https://localhost:7054/MyAPI/authenticated-user")) as string;
    }
    catch (error) {
      console.error(error);
    }
  }
}
