import { Component } from '@angular/core';
import { AuthServiceService } from './auth-service.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: []
})
export class AppComponent {
  title = 'ClientApp';
  displayedMessage: string = "";

  constructor(private _authService: AuthServiceService) { }

  async button0_click() {
    this.displayedMessage = await this._authService.getToken();
  }

  async button1_click() {
    this.displayedMessage = await this._authService.getAdminOnly();
  }

  button2_click() {
    this._authService.getAdminOrManager();
    this.displayedMessage = this._authService.message;
  }

  button3_click() {
    this._authService.getAuthenticatedUser();
    this.displayedMessage = this._authService.message;
  }

}
<router-outlet></router-outlet>

<button (click)="button0_click()">
  GET TOKEN
</button>

<button (click)="button1_click()">
  ADMIN
</button>

<button (click)="button2_click()">
  ADMIN AND MANAGER
</button>

<button (click)="button3_click()">
  AUTHENTICATED USER
</button>


<h1>{{displayedMessage}}</h1>
