import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { lastValueFrom } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthServiceService {
  token: string = "";
  message: string = "";
  constructor(private http: HttpClient) { }

  getToken() {
    var params = new HttpParams().set("username", "admin").set("password", "password");
    //try {
    //  this.token = await lastValueFrom(this.http.get("https://localhost:7054/token", {params})) as string;
    //} catch (error) {
    //  console.error(error);
    //}
    lastValueFrom(this.http.get<string>("https://localhost:7054/token", { params }))
      .then(res => {
        console.log(res);
        this.token = res;
      })
      .catch(err => console.error(err));
  }

  async getAdminOnly() {
    try {
      this.message = await lastValueFrom(this.http.get("https://localhost:7054/MyAPI/admin-only")) as string;
    }
    catch (error) {
      console.error(error);
    }
  }

  async getAdminOrManager() {
    try {
      this.message = await lastValueFrom(this.http.get("https://localhost:7054/MyAPI/manager-or-admin")) as string;
    }
    catch (error) {
      console.error(error);
    }
  }

  async getAuthenticatedUser() {
    try {
      this.message = await lastValueFrom(this.http.get("https://localhost:7054/MyAPI/authenticated-user")) as string;
    }
    catch (error) {
      console.error(error);
    }
  }
}
